import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@/lib/supabase/server'

export async function GET(request: NextRequest) {
    const searchParams = request.nextUrl.searchParams
    const query = searchParams.get('query') || 'Software Engineer'
    const location = searchParams.get('location') || 'United States'
    const remoteOnly = searchParams.get('remote_only') === 'true'
    const employmentTypes = searchParams.get('employment_types') || 'FULLTIME'
    const page = searchParams.get('page') || '1'

    const apiKey = process.env.RAPIDAPI_KEY

    console.log(`[JSearch] Request: query="${query}", location="${location}", remote=${remoteOnly}, type=${employmentTypes}, page=${page}`)

    if (!apiKey || apiKey === 'your_rapidapi_key_here') {
        if (process.env.NODE_ENV === 'development') {
            console.warn('[JSearch] RapidAPI Key is missing or default.')
        }
        return NextResponse.json({
            error: 'RapidAPI Key is not configured correctly. Check your .env.local file.',
            isMock: true,
            data: []
        }, { status: 400 })
    }

    try {
        const supabase = await createClient()
        const { data: { user } } = await supabase.auth.getUser()

        if (!user) {
            return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
        }

        const url = new URL('https://jsearch.p.rapidapi.com/search')
        // JSearch likes the location inside the query for better results
        url.searchParams.set('query', `${query} in ${location}`)
        url.searchParams.set('page', page)
        url.searchParams.set('num_pages', '1')

        if (remoteOnly) {
            url.searchParams.set('remote_jobs_only', 'true')
        }

        url.searchParams.set('employment_types', employmentTypes)

        console.log(`[JSearch] Calling URL: ${url.toString()}`)

        const response = await fetch(url.toString(), {
            method: 'GET',
            headers: {
                'X-RapidAPI-Key': apiKey,
                'X-RapidAPI-Host': 'jsearch.p.rapidapi.com'
            }
        })

        if (!response.ok) {
            const errorText = await response.text()
            console.error(`[JSearch] API Error (${response.status}):`, errorText)
            return NextResponse.json({ error: `JSearch API error: ${response.statusText}` }, { status: response.status })
        }

        const result = await response.json()
        console.log(`[JSearch] Received ${result?.data?.length || 0} jobs.`)

        if (!result.data || !Array.isArray(result.data)) {
            console.error('[JSearch] Unexpected response structure:', result)
            return NextResponse.json({ error: 'Unexpected response from JSearch' }, { status: 500 })
        }

        // Transform the JSearch data to our internal format
        const jobs = result.data.map((job: Record<string, any>) => ({
            id: job.job_id,
            external_job_id: job.job_id,
            title: job.job_title,
            company: job.employer_name,
            company_logo: job.employer_logo,
            location: `${job.job_city ? job.job_city + ', ' : ''}${job.job_state || job.job_country || ''}`,
            description: job.job_description,
            apply_url: job.job_apply_link,
            url: job.job_google_link || job.job_apply_link,
            salary_min: job.job_min_salary,
            salary_max: job.job_max_salary,
            is_remote: job.job_is_remote,
            employment_type: job.job_employment_type?.replace('_', ' '),
            posted_at: job.job_posted_at_datetime_utc,
            match_score: 85, // Default score, will be updated by client-side analysis
            status: 'open'
        }))

        return NextResponse.json({
            success: true,
            data: jobs,
            total: jobs.length
        })

    } catch (error: unknown) {
        const errorMessage = error instanceof Error ? error.message : 'An internal error occurred during job search'
        console.error('[JSearch] Route Crash:', error)
        return NextResponse.json({
            error: errorMessage,
            data: []
        }, { status: 500 })
    }
}

'use client'

import { useState, useEffect, useCallback } from "react"
import JobCard from "@/components/dashboard/JobCard"
import { Briefcase, Search, Loader2, SlidersHorizontal, MapPin } from "lucide-react"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Card } from "@/components/ui/card"
import { toast } from "sonner"
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
} from "@/components/ui/select"

interface JobBoardClientProps {
    initialResumes: { id: string; file_name: string }[]
}

export default function JobBoardClient({ initialResumes }: JobBoardClientProps) {
    const [jobs, setJobs] = useState<Record<string, any>[]>([])
    const [loading, setLoading] = useState(true)
    const [query, setQuery] = useState("Software Engineer")
    const [location, setLocation] = useState("United States")
    const [remoteOnly, setRemoteOnly] = useState(false)
    const [jobType, setJobType] = useState("FULLTIME")
    const [page, setPage] = useState(1)
    const [lastUpdated, setLastUpdated] = useState<Date | null>(null)

    const fetchJobs = useCallback(async () => {
        setLoading(true)
        try {
            const params = new URLSearchParams({
                query,
                location,
                remote_only: remoteOnly.toString(),
                employment_types: jobType,
                page: page.toString()
            })

            const response = await fetch(`/api/jobs/search?${params.toString()}`)
            const result = await response.json()

            if (result.success) {
                setJobs(result.data)
                setLastUpdated(new Date())
            } else {
                toast.error(result.error || "Failed to fetch jobs")
                if (result.isMock) {
                    console.warn("RapidAPI Key missing, please configure .env.local")
                }
            }
        } catch (error) {
            console.error("Error fetching jobs:", error)
            toast.error("An error occurred while fetching jobs")
        } finally {
            setLoading(false)
        }
    }, [query, location, remoteOnly, jobType, page])

    useEffect(() => {
        fetchJobs()
    }, [fetchJobs])

    return (
        <div className="space-y-8">
            <div className="flex flex-col lg:flex-row lg:items-center justify-between gap-6">
                <div>
                    <h2 className="text-3xl font-bold tracking-tight text-slate-900 dark:text-white flex items-center gap-3">
                        Job Board
                        {loading && <Loader2 className="h-6 w-6 animate-spin text-blue-500" />}
                    </h2>
                    <p className="text-slate-500 dark:text-slate-400">
                        {lastUpdated ? `Real-time jobs updated at ${lastUpdated.toLocaleTimeString()}` : 'Searching for the best opportunities...'}
                    </p>
                </div>

                <div className="flex flex-wrap items-center gap-3">
                    <div className="relative w-full sm:w-64">
                        <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-slate-400 z-10" />
                        <Input
                            placeholder="Role (e.g. Frontend)"
                            value={query}
                            onChange={(e) => setQuery(e.target.value)}
                            className="pl-10 rounded-xl"
                        />
                    </div>
                    <div className="relative w-full sm:w-48">
                        <MapPin className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-slate-400 z-10" />
                        <Input
                            placeholder="Location"
                            value={location}
                            onChange={(e) => setLocation(e.target.value)}
                            className="pl-10 rounded-xl"
                        />
                    </div>
                    <Button onClick={() => fetchJobs()} variant="default" className="rounded-xl font-bold bg-blue-600 hover:bg-blue-700">
                        Search
                    </Button>
                </div>
            </div>

            <Card className="p-4 border-slate-200 dark:border-slate-800 bg-slate-50/50 dark:bg-slate-900/50 rounded-2xl">
                <div className="flex flex-wrap items-center gap-4">
                    <div className="flex items-center gap-2">
                        <SlidersHorizontal className="h-4 w-4 text-slate-500" />
                        <span className="text-sm font-medium text-slate-700 dark:text-slate-300">Filters:</span>
                    </div>

                    <Select value={jobType} onValueChange={setJobType}>
                        <SelectTrigger className="w-[140px] rounded-xl h-9">
                            <SelectValue placeholder="Job Type" />
                        </SelectTrigger>
                        <SelectContent>
                            <SelectItem value="FULLTIME">Full-time</SelectItem>
                            <SelectItem value="CONTRACTOR">Contract</SelectItem>
                            <SelectItem value="PARTTIME">Part-time</SelectItem>
                            <SelectItem value="INTERN">Internship</SelectItem>
                        </SelectContent>
                    </Select>

                    <div className="flex items-center gap-2 px-3 py-1.5 rounded-xl border border-slate-200 dark:border-slate-800 bg-white dark:bg-slate-950">
                        <span className="text-xs font-medium">Remote Only</span>
                        <input
                            type="checkbox"
                            checked={remoteOnly}
                            onChange={(e) => setRemoteOnly(e.target.checked)}
                            className="h-4 w-4 rounded border-slate-300 text-blue-600 focus:ring-blue-600"
                        />
                    </div>

                    <div className="ml-auto flex items-center gap-2">
                        <Button
                            variant="outline"
                            size="sm"
                            disabled={page === 1}
                            onClick={() => setPage(p => p - 1)}
                            className="rounded-lg h-8"
                        >
                            Prev
                        </Button>
                        <span className="text-xs font-bold text-slate-500">Page {page}</span>
                        <Button
                            variant="outline"
                            size="sm"
                            onClick={() => setPage(p => p + 1)}
                            className="rounded-lg h-8"
                        >
                            Next
                        </Button>
                    </div>
                </div>
            </Card>

            <div className="grid gap-6">
                {loading ? (
                    Array(5).fill(0).map((_, i) => (
                        <Card key={i} className="p-6 border-slate-200 dark:border-slate-800 rounded-2xl animate-pulse">
                            <div className="flex gap-4">
                                <div className="h-14 w-14 rounded-2xl bg-slate-100 dark:bg-slate-800" />
                                <div className="space-y-2 flex-1">
                                    <div className="h-4 w-1/4 bg-slate-100 dark:bg-slate-800 rounded" />
                                    <div className="h-3 w-1/2 bg-slate-100 dark:bg-slate-800 rounded" />
                                    <div className="h-16 w-full bg-slate-50 dark:bg-slate-900/50 rounded mt-4" />
                                </div>
                            </div>
                        </Card>
                    ))
                ) : jobs.length > 0 ? (
                    jobs.map((job) => (
                        <JobCard key={job.id} job={job} resumes={initialResumes} />
                    ))
                ) : (
                    <div className="p-20 text-center bg-slate-50 dark:bg-slate-900/50 rounded-3xl border-2 border-dashed border-slate-200 dark:border-slate-800">
                        <Briefcase className="h-12 w-12 text-slate-400 mx-auto mb-4" />
                        <h3 className="text-xl font-bold text-slate-900 dark:text-white">No jobs found</h3>
                        <p className="text-slate-500 mb-6">Try adjusting your search filters or keywords.</p>
                        <Button onClick={() => fetchJobs()} variant="outline" className="rounded-xl">
                            Try Again
                        </Button>
                    </div>
                )}
            </div>
        </div>
    )
}

'use client'

import { useState } from 'react'
import { Button } from "@/components/ui/button"
import { Sparkles, Loader2 } from "lucide-react"
import { seedJobs } from './seed-action'
import { useRouter } from 'next/navigation'
import { toast } from 'sonner'

export function SeedButton() {
    const [isLoading, setIsLoading] = useState(false)
    const router = useRouter()

    const handleSeed = async () => {
        setIsLoading(true)
        try {
            const result = await seedJobs()
            if (result.success) {
                toast.success("Jobs seeded successfully!")
                router.refresh()
            } else {
                toast.error(result.error || "Failed to seed jobs")
            }
        } catch (error) {
            toast.error("Something went wrong")
        } finally {
            setIsLoading(false)
        }
    }

    return (
        <Button
            onClick={handleSeed}
            disabled={isLoading}
            className="bg-indigo-600 text-white hover:bg-indigo-700 font-bold px-8 py-6 rounded-2xl shadow-xl"
        >
            {isLoading ? <Loader2 className="h-5 w-5 animate-spin mr-2" /> : <Sparkles className="h-5 w-5 mr-2" />}
            Populate Sample Jobs
        </Button>
    )
}

'use client'

import { useState } from "react"
import { Card, CardContent } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import {
    Building2,
    MapPin,
    DollarSign,
    ExternalLink,
    Loader2,
    CheckCircle2,
    Info,
    TrendingUp,
    XCircle,
    Heart,
    Sparkles,
    Briefcase
} from "lucide-react"
import { toast } from "sonner"
import { applyToJob, toggleSaveJob } from "@/app/dashboard/jobs/actions"
import {
    Sheet,
    SheetContent,
    SheetDescription,
    SheetHeader,
    SheetTitle,
    SheetTrigger,
} from "@/components/ui/sheet"

interface JobCardProps {
    job: any
    resumes: any[]
}

export default function JobCard({ job, resumes }: JobCardProps) {
    const [hasApplied, setHasApplied] = useState(job.applications?.length > 0)
    const [isSaved, setIsSaved] = useState(job.saved_jobs?.length > 0)
    const [analyzing, setAnalyzing] = useState(false)
    const [analysisResults, setAnalysisResults] = useState<any>(null)

    const handleSave = async () => {
        const newSavedStatus = !isSaved
        setIsSaved(newSavedStatus) // Optimistic update
        try {
            const result = await toggleSaveJob(job.id, isSaved)
            if (!result.success) {
                setIsSaved(!newSavedStatus)
                toast.error("Failed to save job.")
            } else {
                toast.success(newSavedStatus ? "Job saved!" : "Job removed.")
            }
        } catch (error) {
            setIsSaved(!newSavedStatus)
            toast.error("An error occurred.")
        }
    }

    const handleApply = async () => {
        if (!resumes || resumes.length === 0) {
            toast.error("Please upload a resume first.")
            return
        }

        if (!hasApplied) {
            setHasApplied(true)
            try {
                const result = await applyToJob(job.id, resumes[0].id)
                if (!result.success) {
                    setHasApplied(false)
                    toast.error("Failed to track application.")
                }
            } catch (error) {
                setHasApplied(false)
                console.error("Tracking failed", error)
            }
        }
    }

    const handleAnalysis = async () => {
        if (!resumes || resumes.length === 0) {
            toast.error("Please upload a resume first.")
            return
        }

        setAnalyzing(true)
        try {
            const response = await fetch('/api/analysis/match', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    resumeId: resumes[0].id,
                    jobDescription: job.description,
                    jobId: job.id
                })
            })

            const result = await response.json()
            if (result.success) {
                setAnalysisResults(result.data)
                toast.success("AI Analysis complete!")
            } else {
                toast.error(result.error || "Analysis failed")
            }
        } catch (error) {
            console.error("Analysis Error:", error)
            toast.error("An error occurred during analysis")
        } finally {
            setAnalyzing(false)
        }
    }

    return (
        <Card className="border-slate-200 dark:border-slate-800 shadow-sm rounded-2xl group hover:border-blue-500 transition-colors overflow-hidden">
            <CardContent className="p-6">
                <div className="flex flex-col md:flex-row justify-between gap-6">
                    <div className="flex gap-4">
                        <div className="h-14 w-14 rounded-2xl bg-slate-50 dark:bg-slate-900 flex items-center justify-center text-slate-400 border border-slate-100 dark:border-slate-800 flex-shrink-0 group-hover:scale-110 transition-transform overflow-hidden">
                            {job.company_logo ? (
                                <img src={job.company_logo} alt={job.company} className="h-full w-full object-contain p-2" />
                            ) : (
                                <Building2 className="h-8 w-8 text-blue-500" />
                            )}
                        </div>
                        <div className="space-y-1">
                            <div className="flex items-center gap-2 flex-wrap">
                                <h4 className="font-bold text-lg text-slate-900 dark:text-white">{job.title}</h4>
                                <Badge variant="secondary" className="bg-emerald-50 text-emerald-700 dark:bg-emerald-900/30 dark:text-emerald-300 border-none text-[10px] uppercase font-bold tracking-wider">
                                    {job.match_score || 85}% Match
                                </Badge>
                                {job.is_remote && (
                                    <Badge variant="outline" className="text-[10px] uppercase border-blue-200 text-blue-600 dark:border-blue-900 bg-blue-50/50 dark:bg-blue-950/50">
                                        Remote
                                    </Badge>
                                )}
                                {job.employment_type && (
                                    <Badge variant="outline" className="text-[10px] uppercase border-slate-200 text-slate-500">
                                        {job.employment_type}
                                    </Badge>
                                )}
                            </div>
                            <div className="flex flex-wrap items-center gap-x-4 gap-y-1 text-sm text-slate-500">
                                <span className="font-semibold text-slate-700 dark:text-slate-300">{job.company}</span>
                                <div className="flex items-center gap-1">
                                    <MapPin className="h-3 w-3" />
                                    {job.location}
                                </div>
                                {(job.salary_min || job.salary_max) && (
                                    <div className="flex items-center gap-1">
                                        <DollarSign className="h-3 w-3" />
                                        {job.salary_min && `$${job.salary_min.toLocaleString()}`}{job.salary_min && job.salary_max && ' - '}{job.salary_max && `$${job.salary_max.toLocaleString()}`}
                                    </div>
                                )}
                            </div>
                            <p className="text-sm text-slate-500 dark:text-slate-400 line-clamp-2 mt-2 leading-relaxed">
                                {job.description}
                            </p>
                        </div>
                    </div>
                    <div className="flex flex-col gap-2 min-w-[140px]">
                        <a
                            href={job.apply_url || job.url}
                            target="_blank"
                            rel="noopener noreferrer"
                            onClick={() => {
                                if (resumes && resumes.length > 0) {
                                    handleApply()
                                } else {
                                    toast.error("Please upload a resume first.")
                                }
                            }}
                            className={`flex items-center justify-center rounded-xl h-11 font-bold text-sm px-4 transition-all shadow-sm ${hasApplied
                                ? "bg-emerald-500 hover:bg-emerald-600 text-white"
                                : "bg-slate-900 dark:bg-white dark:text-slate-900 text-white hover:bg-slate-800"
                                }`}
                        >
                            {hasApplied ? (
                                <>
                                    <CheckCircle2 className="h-4 w-4 mr-2" />
                                    Applied
                                </>
                            ) : (
                                <>
                                    Apply Now
                                    <ExternalLink className="h-4 w-4 ml-2 italic" />
                                </>
                            )}
                        </a>

                        <div className="flex gap-2">
                            <Button
                                variant="outline"
                                size="icon"
                                onClick={handleSave}
                                className={`rounded-xl h-11 w-11 transition-all ${isSaved ? "border-red-100 bg-red-50 text-red-500 hover:bg-red-100 dark:bg-red-950/30 dark:border-red-900" : ""}`}
                            >
                                <Heart className={`h-5 w-5 ${isSaved ? "fill-current" : ""}`} />
                            </Button>
                            <Sheet>
                                <SheetTrigger asChild>
                                    <Button variant="outline" className="rounded-xl h-11 text-xs gap-1 flex-1">
                                        <Sparkles className="h-3 w-3 text-blue-500" />
                                        AI Match Details
                                    </Button>
                                </SheetTrigger>
                                <SheetContent className="sm:max-w-md lg:max-w-xl overflow-y-auto">
                                    <SheetHeader className="space-y-4 pb-6 border-b text-left">
                                        <div className="flex items-center gap-3">
                                            <div className="h-12 w-12 rounded-xl bg-blue-50 dark:bg-blue-900/30 flex items-center justify-center overflow-hidden">
                                                {job.company_logo ? (
                                                    <img src={job.company_logo} alt={job.company} className="h-full w-full object-contain p-1" />
                                                ) : (
                                                    <Building2 className="h-6 w-6 text-blue-600" />
                                                )}
                                            </div>
                                            <div>
                                                <SheetTitle className="text-xl text-left font-bold">{job.title}</SheetTitle>
                                                <SheetDescription className="text-blue-600 font-medium text-left">
                                                    {job.company}
                                                </SheetDescription>
                                            </div>
                                        </div>
                                        <div className="flex flex-wrap gap-2">
                                            <Badge variant="outline" className="flex gap-1 items-center px-3 py-1">
                                                <MapPin className="h-3 w-3" /> {job.location}
                                            </Badge>
                                            {job.match_score && (
                                                <Badge variant="secondary" className="flex gap-1 items-center px-3 py-1 bg-emerald-50 text-emerald-700">
                                                    <Sparkles className="h-3 w-3" /> {job.match_score}% Match
                                                </Badge>
                                            )}
                                        </div>
                                    </SheetHeader>

                                    <div className="space-y-8 py-8">
                                        <div className="space-y-4">
                                            <h4 className="font-bold text-slate-900 dark:text-white flex items-center gap-2">
                                                <Sparkles className="h-4 w-4 text-blue-500" />
                                                AI Analysis & Matching
                                            </h4>
                                            <p className="text-xs text-slate-500 mb-4 italic">Analysis based on your most recent resume.</p>

                                            {!analysisResults ? (
                                                <div className="p-5 rounded-2xl bg-blue-50/50 dark:bg-blue-900/10 border border-blue-100/50 dark:border-blue-900/30 text-sm text-slate-700 dark:text-slate-300">
                                                    <div className="flex items-center gap-3 mb-4 text-blue-700 dark:text-blue-400">
                                                        <Info className="h-5 w-5" />
                                                        <span className="font-semibold">Match score is an estimate.</span>
                                                    </div>
                                                    Generate a deep-dive analysis to see missing skills and suggestions.
                                                    <Button
                                                        onClick={handleAnalysis}
                                                        disabled={analyzing}
                                                        className="w-full mt-4 h-12 rounded-xl bg-blue-600 hover:bg-blue-700 font-bold shadow-lg shadow-blue-500/20 transition-all"
                                                    >
                                                        {analyzing ? (
                                                            <>
                                                                <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                                                                Analyzing Profile...
                                                            </>
                                                        ) : "Run Full AI Analysis"}
                                                    </Button>
                                                </div>
                                            ) : (
                                                <div className="space-y-6">
                                                    <div className="flex items-center justify-between p-4 rounded-2xl bg-emerald-50 dark:bg-emerald-900/20 border border-emerald-100 dark:border-emerald-900/30">
                                                        <div className="space-y-1">
                                                            <span className="text-xs font-bold text-emerald-600 dark:text-emerald-400 uppercase tracking-wider">Semantic Match Score</span>
                                                            <div className="text-3xl font-black text-emerald-700 dark:text-emerald-300">{analysisResults.match_score}%</div>
                                                        </div>
                                                    </div>

                                                    <div className="space-y-3">
                                                        <h5 className="text-sm font-bold flex items-center gap-2">
                                                            <div className="h-1.5 w-1.5 rounded-full bg-emerald-500" />
                                                            Matching Skills
                                                        </h5>
                                                        <div className="flex flex-wrap gap-2">
                                                            {analysisResults.matching_skills?.map((skill: string, i: number) => (
                                                                <Badge key={i} variant="secondary" className="bg-emerald-100/50 text-emerald-700 hover:bg-emerald-100 dark:bg-emerald-900/30 dark:text-emerald-300 border-none px-2 py-0.5 text-[10px]">
                                                                    {skill}
                                                                </Badge>
                                                            ))}
                                                        </div>
                                                    </div>

                                                    <div className="space-y-3">
                                                        <h5 className="text-sm font-bold flex items-center gap-2">
                                                            <div className="h-1.5 w-1.5 rounded-full bg-red-500" />
                                                            Missing Skills (Gaps)
                                                        </h5>
                                                        <div className="flex flex-wrap gap-2">
                                                            {analysisResults.missing_skills?.map((skill: string, i: number) => (
                                                                <Badge key={i} variant="secondary" className="bg-red-50 text-red-600 hover:bg-red-100 dark:bg-red-900/20 dark:text-red-400 border-none px-2 py-0.5 text-[10px]">
                                                                    {skill}
                                                                </Badge>
                                                            ))}
